window.addEventListener("DOMContentLoaded",(()=>{document.querySelector("circle").addEventListener("mouseenter",(t=>{t.target.style.fill=function(){const t=Math.round(255*Math.random()).toString(16).padStart(2,"0"),n=Math.round(255*Math.random()).toString(16).padStart(2,"0"),r=Math.round(255*Math.random()).toString(16).padStart(2,"0");return"#".concat(t.toString(),n.toString(),r.toString())}()}))}));
